jcr:root.manageTabs:
function(tab,noSwitch)
{
	var tabs=['image','imgdesc','list','imagelefttextright','imageleftlinkright','imagecentertextbelow','smallimageleftwithlinkortext'];
	var index=tabs.indexOf(tab);
	if(index==-1)
		return;
	for(var i=3;i<tabs.length;i++)
	{
		if(index==i)
		{
			this.unhideTabStripItem(i);
		}
		else
		{
			this.hideTabStripItem(i);
		}
	}
	this.doLayout();
	if(!noSwitch)
		this.activate(index);
}

listeners.selectionchanged:
function(box,value){
var dlg = box.findParentByType('panel');
if(value == "Text")
{
dlg.getComponent('textID').show();
dlg.getComponent('colorID').show();
dlg.getComponent('linkID').hide();
dlg.getComponent('pathID').hide();
dlg.getComponent('borderID').hide();
}
else{
dlg.getComponent('linkID').show();
dlg.getComponent('pathID').show();
dlg.getComponent('borderID').show();
dlg.getComponent('textID').hide();
dlg.getComponent('colorID').hide();
}
}
